mapper.select:
select * from lpost where lposttitle like
('%${keyword}%')
order by lpostnum desc limit #{startRow},#{pageSize}

mapper.update:
update lpost
set
lposttitle=#{lposttitle}, lpostcontents=#{lpostcontents},
lpostregdate=now(), deadline=#{deadline}
where lpostnum=#{lpostnum}

UPDATE lpost
SET lpostaddr = #{roadAddress}
WHERE lpostnum = #{boardnum}
